const useStyles=makeStyles({
    gridroot:{
        padding: '2px',
        background: 'linear-gradient(45deg, #FE6B8B 30%, #FF8E53 90%)',
        width:'60%',
        marginLeft:'20%'
    },
    numbers:{
       margin: '20%',
       textAlign:"center",
       height:'20px',
       width:'20px',
       fontSize:'10px',
        verticalAlign:'center',
        paddingTop:'4px'
    }

});
const GameBoard = ({ gameBoard }) => {
    var array = new Array(90);
    for (var i = 1; i <= 90; i++) {
        array[i - 1] = i;
    }
    const classes=useStyles();

    return (
        // <RenderGrid array={array} gameBoard={gameBoard} />
        <Grid container justify="center" classes={{root:classes.gridroot}}>
        {array.map((item) => {
            return (
                <Grid item xs={1}>
                    <Paper classes={{root:classes.numbers}} className={gameBoard.includes(item)?"done":"todo"}>{item}</Paper>
                </Grid>
            )
        })}
    </Grid>
    )
}
export default GameBoard;
