import React, {useState} from 'react'
import {Link} from 'react-router-dom'
const Join=()=>{
    const [name,setName]=useState("");
    const [room,setRoom]=useState("");
    return(
    <div>
        <h3>JOIN</h3>
        <p><input type='text' value={name} onChange={(event)=>setName(event.target.value)} placeholder="name"/></p>
        <p><input type='text' value={room} onChange={(event)=>setName(event.target.value)} placeholder="room"/></p>
        <Link onClick={event=>(!name||!room) ? event.preventDefault():null} to={`/home?name=${name}&room=${room}`}>
            <button className='button mt-20' type='submit'>Sign in</button>
        </Link>
    </div>
    )
}
