export default function generate() {
    var ticket = new Array(3);
    for (var i = 0; i < 3; i++) {
        ticket[i] = new Array(9);
    }
    //for each row we generate 5 random indexes
    var indexMatrix = new Array(3);
    for (var i = 0; i < 3; i++) {
        indexMatrix[i] = new Array();
    }
    for (var j = 0; j < 3; j++) {
        for (var i = 0; i < 5; i++) {
            var index = Math.floor(Math.random() * 9);
            if (indexMatrix[j].includes(index))
                i--;
            else {
                indexMatrix[j].push(index);
            }
        }
    }
    // for (var i = 0; i < 3; i++) {
    //     for (var j = 0; j < 5; j++) {
    //         console.log(indexMatrix[i][j]);
    //     }
    // }
    var numbers = new Array();
    for (var i = 0; i < 3; i++) {
        for (var j = 0; j < 5; j++) {
            var offset = Math.floor(Math.random() * 10);
            console.log('offset=' + offset);
            var indexOffset = indexMatrix[i][j];
            console.log('Indiexoffset=' + indexOffset);
            var number = indexOffset * 10 + offset;
            console.log('number=' + number);
            if (numbers.includes(number) || number > 99 || number < 1) {
                j--;
                continue;
            }
            ticket[i][indexOffset] = number;
            numbers.push(number);
        }
    }
    // for (var i = 0; i < 3; i++) {
    //     console.log(ticket[i].toString());
    // }
    for (var col = 0; col < 9; col++) {
        if (ticket[0][col] != null && ticket[1][col] != null && ticket[2][col] != null) {
            for (var row = 0; row < 2; row++) {
                if (ticket[row][col] > ticket[row + 1][col]) {
                    var temp = ticket[row][col];
                    ticket[row][col] = ticket[row + 1][col];
                    ticket[row + 1][col] = temp;
                }

            }
        }


        // if 1st && 2nd row are filled by r && om number

        else if (ticket[0][col] != null && ticket[1][col] != null && ticket[2][col] == null) {
            if (ticket[0][col] > ticket[1][col]) {
                var temp = ticket[0][col];
                ticket[0][col] = ticket[1][col];
                ticket[1][col] = temp;
            }
        }


        // if 1st && 3rd row are filled by r && om number

        else if (ticket[0][col] != null && ticket[2][col] != null && ticket[1][col] == null) {
            if (ticket[0][col] > ticket[2][col]) {
                var temp = ticket[0][col];
                ticket[0][col] = ticket[2][col];
                ticket[2][col] = temp;
            }
        }

        // if 2nd && 3rd rows are filled with r && om numbers

        else if (ticket[0][col] == null && ticket[1][col] != null && ticket[2][col] != null) {
            if (ticket[1][col] > ticket[2][col]) {
                var temp = ticket[1][col];
                ticket[1][col] = ticket[2][col];
                ticket[2][col] = temp;
            }
        }

    }
    for (var i = 0; i < 3; i++) {
        for(var j=0;j<9;j++){
            if(ticket[i][j]==null){
                ticket[i][j]=0;
            }
        }
    }
    return ticket;
}
