import React,{useState} from 'react'
import Grid from '@material-ui/core/Grid'
import Paper from '@material-ui/core/Paper'
import { makeStyles } from '@material-ui/core/styles';
import './Ticket.css'
const useStyles=makeStyles({
    selectedCell:{
        background:'#efefef'
    },
    notSelected:{
        background:'#ffffff'
    }

});
const Ticket = ({ ticket }) => {
    //console.log({row1}.toString());
    const classes=useStyles();
    return (
        <div className='ticketPaper'>
            {ticket.map((row) => {
                return (
                    <Grid container justify="center" className='rowOfTicket' spacing={2}>
                        {row.map((item) => {
                            const [selected,setSelected]=useState(false);
                            if (item != 0) {
                                return (
                                    <Grid item xs={1.33}>
                                    <Paper onClick={()=>{

                                        if({selected}){
                                            alert({selected});
                                            setSelected(true);
                                        }
                                        else{
                                            setSelected(false);
                                        }
                                    }} classes={{selected}==true?{root:classes.selectedCell}:{root:classes.notSelected}}>{item}</Paper>
                                    </Grid>
                                )
                            }
                            else {
                                return (
                                    <Grid item xs={1.33}>
                                    <Paper>{"  "}</Paper>
                                    </Grid>
                                )
                            }
                        })}
                    </Grid>
                )
            })}
        </div>
    )
}
export default Ticket;
